import React, { useState, useEffect } from 'react';
import styled from "styled-components"

const Navbar = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Nav scrollPosition={scrollPosition}>
      <NavGroup>
        <NavLink>Test 1</NavLink>
        <NavLink>Test 2</NavLink>
        <NavLink>Test 3</NavLink>
        <NavLink>Test 4</NavLink>
      </NavGroup>
    </Nav>
  )
}

const Nav = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 50px;
  z-index: 2;
  display: flex;
  background: white;
  align-items: center;
  justify-content: space-between;
  transition: height 0.5s, box-shadow 0.3s;
  // a {
  //   transition: color 0.3s;
  // }
  ${props => props.scrollPosition > 50 ? 
    `
      background: rgba(0, 0, 0, 0.14);
      box-shadow: 0 10px 50px rgba(0, 0, 1);
      height: 80px;
      // a {
      //   color: white
      // }
    ` : ''
  };
`;

const NavGroup = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-left: 10px;
`;

const NavLink = styled.a`
  margin: 0 5px;
  color: black;
`;

export default Navbar
export {Navbar}
